mapper.select:
select r.sid, r.organ_depart_sid orgDepartSid, r.real_name realName, r.user_name userName, r.user_pwd passWord, r.status,r.type,ur.role_sid roleSid from users r left join user_role ur on r.sid=ur.users_sid
1=1
and r.user_name like "%"#{userName}"%"
and r.type = #{type}
order by r.sid limit #{start},#{pageSize}

mapper.update:
update user_role
set role_sid = #{roleSid,jdbcType=INTEGER}
where users_sid = #{userSid,jdbcType=INTEGER}
