mapper.select:
	select 

  from mobile_flash_promotions
  where
  	start_time <= #{currentTime,jdbcType=TIMESTAMP} 
  	and end_time >= #{currentTime,jdbcType=TIMESTAMP} 
  	and flag=#{flag,jdbcType=INTEGER}

  order by end_time asc

  	select

  	from mobile_flash_promotions


  			and sid = #{sid}


			and title like "%"#{title,jdbcType=VARCHAR}"%"


			and start_time >= DATE_FORMAT(#{startTime},'%Y-%m-%d %T')


			and end_time <= DATE_FORMAT(#{endTime},'%Y-%m-%d %T')


		 	and flag = #{flag}


		 	and promotion_type_sid = #{promotionTypeSid}



  	order by sid desc
  	limit
  		#{startRecords},#{pageSize}

  	select count(sid)

  	from mobile_flash_promotions


  			sid = #{sid}


			and title like "%"#{title,jdbcType=VARCHAR}"%"



			and start_time >= DATE_FORMAT(#{startTime},'%Y-%m-%d %T')


			and end_time <= DATE_FORMAT(#{endTime},'%Y-%m-%d %T')


		 	and flag = #{flag}


		 	and promotion_type_sid = #{promotionTypeSid}
