mapper.select:
select
from promotions where sid in
#{item}

  	select

  	from promotions


  			sid = #{proSid}


			and promotion_title like "%"#{title,jdbcType=VARCHAR}"%"


			and promotion_begin_time >= DATE_FORMAT(#{startTime},'%Y-%m-%d %T')


			and promotion_end_time <= DATE_FORMAT(#{endTime},'%Y-%m-%d %T')


		 	and promotion_status = ${proStatus}


			and promotion_type_sid = #{promotionTypeSid}


			and del_flag = #{delFlag}


			and creater = #{creater}


  	order 
  		by sid desc
  	limit
  		#{startRecords},#{pageSize}

  	select count(sid)

  	from promotions


  			and sid = #{proSid}


			and promotion_title like "%"#{title,jdbcType=VARCHAR}"%"


			and promotion_begin_time >= DATE_FORMAT(#{startTime},'%Y-%m-%d %T')


			and promotion_end_time <= DATE_FORMAT(#{endTime},'%Y-%m-%d %T')


		 	and promotion_status = #{proStatus}


			and promotion_type_sid = #{promotionTypeSid}


			and del_flag = #{delFlag}
